import { useForm } from "react-hook-form";
import { useState } from "react";
import { useNavigate  } from "react-router-dom";
import { api } from "../../Services/Api";
import Input from "../../Components/Input";
import Button from "../../Components/Button";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { TitleLogin, Container, Wrapper, CriarText } from "./styles"

const schema = yup.object({
  nome: yup.string().required('Campo obrigat贸rio'),
  telefone: yup.string().required('Campo obrigat贸rio'),
}).required();

function CriarConta() {
      const navigate = useNavigate();


  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
    mode: "onChange",
  });
 
  const [cliente, setCliente] = useState({
    id: '',
    nome: '',
    telefone: ''
  })
    
  const handleChange = e => {
    const { name, value } = e.target;
    setCliente({
      ...cliente, [name]: value
    });
    
    console.log(cliente)
  }

  const clientePost = async () => {
    delete cliente.id
    const { data } = await api.post(`CriarCliente`, cliente)
      .then(response => { sessionStorage.setItem('id', response.data.id) }
      );
    console.log(data);
    if (data > 0) {
      navigate('/pedido')
    }
      
  }


    return (<>
    <h1>Criar Conta Teste</h1>
    
    </>)
}

export {CriarConta}
