import { useEffect, useState } from "react"
import { api } from "../../Services/Api"
import CardPedido from "../../Components/CardPedido"
function Administrador() {
    const [cardapio, setCardapio] = useState([])
    const [misturas, setMisturas] = useState([])
    const [guarnicoes, setGuarnicoes] = useState([])

    useEffect(() => {
    
        const listCardapio = async () => {
    
        const response = await api.get(`Cardapio`)
        
            //console.log(response.data)
            setCardapio(response.data)
            console.log(response)
    }
    listCardapio()
}, [])
    
    

    return (<>
    
        <h1>Administrador teste</h1>
        


        {cardapio.map((value, index) =>
        (<>

            <CardPedido key={value.misturas.length} title={value.misturas}>

            </CardPedido>
            <p>{value.misturas}</p>
            <p>{value.guarnicoes}</p>

            
        </>            
        )
        )}

    </>)
}

export {Administrador}
