import { useForm } from "react-hook-form";
import { useState } from "react";
import { useNavigate  } from "react-router-dom";
import { api } from "../../Services/Api";
import Input from "../../Components/Input";
import Button from "../../Components/Button";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import {  TitleLogin, Container, Wrapper, CriarText } from "./styles"
import { useAuth } from "../../Context/AuthProvider/useAuth";


function Login() {
  const navigate = useNavigate();
  const auth = useAuth()

  const onFinish = async (values) => {
    try {
     await auth.authenticate(values.nome, values.telefone)
    } catch (error) {
       alert('Falha no Login' + error)
    }
  }


  const handleCriarConta = () => {
    navigate('/criarconta')
      
  }
  
 
  const [cliente, setCliente] = useState({
    id: '',
    nome: '',
    telefone: ''
  })
    
  const handleChange = e => {
    const { name, value } = e.target;
    setCliente({
      ...cliente, [name]: value
    });
    
  }


  const clienteLoginPost = async (e) => {
    e.preventDefault();
    try {
      delete cliente.id
      if (cliente.nome == null || cliente.telefone == null) {
        alert("Insira o nome e o telefone")
      } else {
        const response = await api.post(`Cliente/Login`, cliente)
        if (response.data.perfil === "Adm") {
          navigate('/administrador')
            
        } else {
          navigate('/pedido')
          
          }
      }
    } 
  catch (error) {
    alert('Falha no Login' + error)
  }
}
    return (
        <>
            <Container>   
                <Wrapper>
                    
                    <TitleLogin>Fa√ßa seu Login</TitleLogin>

            <form onSubmit={(onFinish)}>
                <Input name="nome"   placeholder="Nome:" />    
                <Input name="telefone"  placeholder="Telefone" />   
                
                <Button title="Login"   type="submit"/>
                <CriarText href="/criarconta"> Criar conta</CriarText>

            </form>
            
                    </Wrapper>   
            </Container>
        
        
        </>

    )
}
export {Login}
