import { useForm } from "react-hook-form";
import { useState } from "react";

import { api } from "../../Services/Api";
import Input from "../../Components/Input";
import Button from "../../Components/Button";
import { TitleLogin, Container, Wrapper } from "./styles"

function Login() {

    const {
        control,
        handleSubmit,        
        formState: { errors },
    } = useForm();
      const onSubmit = data => console.log(data)

    
      const [cliente, setCliente] = useState({
            id:'',
            nome: '',
            telefone: ''   
  })

  const clientePost =  async () => {
    delete cliente.id
    if (cliente.nome == null || cliente.telefone == null) {
      alert("Insira o nome e o telefone")
    } else {
      await api.post(`CriarCliente`, cliente).then(response => {
      sessionStorage.setItem('id', response.data.id)              
      });

    }
  }
    return (
        <>
            <Container>
                
                <Wrapper>
                    
                    <TitleLogin>Fa√ßa seu Login</TitleLogin>

            <form>
                <Input name="nome" control={control} placeholder="Nome:" />    
                <Input name="telefone" control={control} placeholder="Telefone" />   
                
                <Button title="Entrar" onClick={() => clientePost()}  type="submit"/>

            </form>

    
                    </Wrapper>
                
            </Container>
        
        
        </>

    )
}
export {Login}
