import {  Container,  Content } from "./styles"
import CardPedido from "../../Components/CardPedido"
import Header from "../../Components/Header"
import { useEffect, useState } from "react"
import { api } from "../../Services/Api"

function Marmita() {
    const [counter, setCounter] = useState(0)
    const dayweek = new Date().toLocaleString('pt-BR', {weekday: 'long'})
    const [cardapio, setCardapio] = useState([])
    const [misturas, setMisturas] = useState([])
    const [guarnicoes, setGuarnicoes] = useState([])

    
const numeros = ["panela", "chicara", 3];
const dobrados = numeros.map(num => num * 2);

console.log(dobrados); 

        useEffect(() => {
    
            const listCardapio = async () => {
        
            const response = await api.get(`Cardapio`)
    
                setCardapio(response.data)
            }
                
            listCardapio()
    }, [])
    
    const handlerCounterPlus = () => {
        setCounter((prev) => prev + 1)
        }
    
    const handlerCounterLess = () => {
        if (counter > 0)
        {
            setCounter(counter - 1)
        } 
    }

    
    return (<>

        <Container>
            <Content>
                
                <Header title={dayweek} /> 
                
                <CardPedido title="aqui esta um pedido " value={counter}
                    onClickPlus={handlerCounterPlus} onClickLess={handlerCounterLess} />
                
                
               {cardapio.map(({ id, misturas, guarnicoes }) =>
                       (
                           <div key={id}>
                               {misturas.map((item, index) => (
                                   <CardPedido key={index} title={item} />
                               )
                               )}
               
                               {guarnicoes.map((item, index) => (
                                   <CardPedido key={index} title={item}/>
                               )
                               )}
                               
                           </div>
                           )
                       
                       )}
                                                                  
            </Content>
        </Container>
    
    </>)
}

export {Marmita}
