import styled from "styled-components";

export const Container = styled.main`
    width: 100vw;
    height: 100vh;

    
    position: relative;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
`

export const Wrapper = styled.div`
    max-width: 300px;
    max-height: 900px;
    border: solid 2px #F2A516;
    border-radius: 8px;
    padding: 66px 22px;

`
export const Column = styled.div`
    flex:1;
`
