import styled from "styled-components";

export const Container = styled.main`
    width:100%;
    max-width:80%;

    
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items:center;
    position: relative;
`

export const Wrapper = styled.div`
    max-width: 300px;
    border: solid 2px #F2A516;
    border-radius: 8px;
    padding: 66px 22px;
    position: absolute;  
    margin:0 auto;  
`
export const Column = styled.div`
    flex:1;
`
