import { useForm } from "react-hook-form";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { api } from "../../Services/Api";
import Input from "../../Components/Input";
import Button from "../../Components/Button";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { TitleLogin, Container, Wrapper } from "./styles"

const schema = yup.object({
  name: yup.string().email('email não é válido').required('Campo obrigatório'),
  telefone: yup.string().min(9, 'No minimo 9 caracteres').required('Campo obrigatório'),
}).required();

function Login() {

    const navigate = useNavigate();

    const {
        control,
        handleSubmit,        
        formState: { errors },
    } = useForm();
      const onSubmit = data => console.log(data)

    
      const [cliente, setCliente] = useState({
            id:'',
            nome: '',
            telefone: ''   
      })
    
  const handleChange = e => {
    const { name, value } = e.target;
    setCliente({
      ...cliente, [name]: value
    });
    
    console.log(cliente)
  }    

  const clientePost = async () => {
    delete cliente.id
      await api.post(`CriarCliente`, cliente)
      .then(response => { sessionStorage.setItem('id', response.data.id) });
   
  }
    return (
        <>
            <Container>
                
                <Wrapper>
                    
                    <TitleLogin>Faça seu Login</TitleLogin>

            <form onSubmit={handleSubmit(clientePost)}>
                <Input name="nome"  control={control} placeholder="Nome:" />    
                <Input name="telefone"  control={control} placeholder="Telefone" />   
                
                <Button title="Entrar"   type="submit"/>

            </form>

    
                    </Wrapper>
                
            </Container>
        
        
        </>

    )
}
export {Login}
