import styled from "styled-components";


export const Container = styled.main`
    width: 100vw;
    height: 100vh;
    
    position: relative;
    display: grid;
    
`

export const Content = styled.div`

    width: 80%;
    height: 100%; 
    display: flex;
    flex-direction: column;
    justify-content: center;
  
    
`
