        if (counter > 0)
        {
            setCounter(counter - 1)
        } 
    }

    const handlerTeste = () => {
        const novoNumero = testeArray.length > 0 ? testeArray[testeArray.length - 1] + 1 : 1;
        setTesteArray([...testeArray])
        console.log(testeArray[0])
