import { useForm } from "react-hook-form";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { api } from "../../Services/Api";
import Input from "../../Components/Input";
import Button from "../../Components/Button";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import {  TitleLogin, Container, Wrapper } from "./styles"

const schema = yup.object({
  nome: yup.string().required('Campo obrigatório'),
  telefone: yup.string().required('Campo obrigatório'),
}).required();

function Login() {

    const navigate = useNavigate();
    const handleClickSignIn = async () => {
      await navigate('/login')
  }
    const {
        control,
        handleSubmit,        
        formState: { errors },
    } = useForm({
      resolver: yupResolver(schema),
      mode: "onSubmit",
    });
 
      const [cliente, setCliente] = useState({
            id:'',
            nome: '',
            telefone: ''   
      })
    
  const handleChange = e => {
    const { name, value } = e.target;
    setCliente({
      ...cliente, [name]: value
    });
    
    console.log(cliente)
  }    

  const clientePost = async () => {
    delete cliente.id
    await api.post(`CriarCliente`, cliente)
      .then(response => { sessionStorage.setItem('id', response.data.id) });

      await  navigate('/pedido')
      
  }
    return (
        <>
            <Container>
                
                <Wrapper>
                    
                    <TitleLogin>Faça seu Login</TitleLogin>

            <form onSubmit={handleSubmit(clientePost)}>
                <Input name="nome" onChange={handleChange} errorMessage={errors?.nome?.message}  control={control} placeholder="Nome:" />    
                <Input name="telefone" onChange={handleChange} errorMessage={errors?.telefone?.message} control={control} placeholder="Telefone" />   
                
                <Button title="Login"   type="submit"/>

            </form>


    
                    </Wrapper>
                
            </Container>
        
        
        </>

    )
}
export {Login}
