import {  Container,  Content } from "./styles"
import CardPedido from "../../Components/CardPedido"
import Header from "../../Components/Header"
import { useEffect, useState } from "react"
import { api } from "../../Services/Api"

function Marmita() {
    const [counter, setCounter] = useState(0)
    const dayweek = new Date().toLocaleString('pt-BR', {weekday: 'long'})
    const [testeArray, setTesteArray] = useState([0])

     const [cardapio, setCardapio] = useState([])
    
        useEffect(() => {
    
            const listCardapio = async () => {
        
            const response = await api.get(`Cardapio`)
    
                setCardapio(response.data)
                
            }
                
            listCardapio()
    
    }, [])
    
    const handlerCounterPlus = () => {
        setCounter((prev) => prev + 1)
        }
    
    const handlerCounterLess = () => {
        if (counter > 0)
        {
            setCounter(counter - 1)
        } 
    }

    const handlerTeste = () => {
        
        setTesteArray((prev) => [
            ...prev, prev++
        ])
        console.log(testeArray)

    }


    
    return (<>

        <Container>
            <Content>
                
                <Header  title={dayweek}/> 
                <CardPedido title="aqui esta um pedido " value={counter}
                    onClickPlus={handlerCounterPlus} onClickLess={handlerCounterLess} />
                
                                                               
            </Content>
        </Container>
    
    
    </>)
}


export {Marmita}
