import { useEffect, useState } from "react"
import { api } from "../../Services/Api"
import CardPedido from "../../Components/CardPedido"
function Administrador() {
    const [cardapio, setCardapio] = useState([])

    useEffect(() => {

        const listCardapio = async () => {
    
            const response = await api.get(`Cardapio`)

            setCardapio(response.data)
            
        }
            
        listCardapio()

    }, [])

    
    
        

// Suponha que você tenha 5 misturas
  const [countMisturas, setCountMisturas] = useState([0, 0, 0, 0, 0]);

  const incrementar = (index) => {
    const novoArray = [...countMisturas];
    novoArray[index] += 1;
    setCountMisturas(novoArray);
  };

  const decrementar = (index) => {
    const novoArray = [...countMisturas];
    if (novoArray[index] > 0) {
      novoArray[index] -= 1;
      setCountMisturas(novoArray);
    }
  };

  return (
    <div>
      <h2>Contadores de Misturas</h2>
      {countMisturas.map((valor, index) => (
        <div key={index}>
          <span>Mistura {index + 1}: {valor}</span>
          <button onClick={() => incrementar(index)}>+</button>
          <button onClick={() => decrementar(index)}>-</button>
        </div>
      ))}
    </div>
  );

}


export {Administrador}
